import React from "react";
import { Table, Button } from "react-bootstrap";
import useInitialState from "../utils/hooks/useInitalState";
const API = "http://localhost:3004/";

function TableApi() {
  const userDelete = (idUser) => {
    var requestOptions = {
      method: "DELETE",
      redirect: "follow",
    };

    fetch("http://localhost:3004/usuario/2", requestOptions)
      .then((response) => response.text())
      .then((result) => console.log(result))
      .catch((error) => console.log("error", error));
    const apiDelete = "http://localhost:3004/usuario/" + idUser;
    console.log(apiDelete);
  };
  const initialState = useInitialState(API);
  return initialState === 0 ? (
    <h1>Loading....</h1>
  ) : (
    <Table striped bordered hover variant="dark">
      <thead>
        <tr>
          <th>#</th>
          <th>Nombre</th>
          <th>Apellido Paterno</th>
          <th>Apellido Materno</th>
          <th>C.I</th>
          <th>Genero</th>
          <th>Direccion</th>
          <th>Modificar</th>
          <th>Eliminar</th>
        </tr>
      </thead>
      <tbody>
        {initialState.usuarios.length > 0 &&
          initialState.usuarios.map((usuario) => (
            <React.Fragment key={usuario.id_usuario}>
              <tr>
                <th>{usuario.id_usuario}</th>
                <th>{usuario.nombre}</th>
                <th>{usuario.apellido_paterno}</th>
                <th>{usuario.apellido_materno}</th>
                <th>{usuario.ci}</th>
                <th>{usuario.genero}</th>
                <th>{usuario.direccion}</th>
                <th>
                  <Button variant="success">Modificar</Button>
                </th>
                <th>
                  <Button
                    variant="danger"
                    onClick={() => userDelete(usuario.id_usuario)}
                  >
                    Eliminar
                  </Button>
                </th>
              </tr>
            </React.Fragment>
          ))}
      </tbody>
    </Table>
  );
}

export default TableApi;
