import React, { useState } from "react";
import { Modal, Button, Form, Container } from "react-bootstrap";

function ModalView({
  nombre,
  apellido_paterno,
  apellido_materno,
  ci,
  genero,
  direccion,
}) {
  const [name, setName] = nombre;
  const [lastName, setlastName] = apellido_paterno;
  const [secondLastName, setsecondLasName] = apellido_materno;
  const [cii, setCii] = ci;
  const [gender, setGender] = genero;
  const [city, setCity] = direccion;

  const [show, setShow] = useState(false);

  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);

  const [form, setValues] = useState({
    nombre: "",
  });
  const handleInput = (event) => {
    setValues({
      ...form,
      [event.target.name]: event.target.value,
    });
  };
  console.log(form);
  const handleSubmit = (event) => {
    //matar el envio del formulario con prevent DEFAUTL
    console.log("enviado");
  };
  return (
    <>
      <Button variant="success" onClick={handleShow}>
        Modificar
      </Button>

      <Modal show={show} onHide={handleClose}>
        <Modal.Header closeButton>
          <Modal.Title>Modificar</Modal.Title>
        </Modal.Header>
        <Container fluid>
          <Form onSubmit={handleSubmit}>
            <Form.Group controlId="formBasicEmail">
              <Form.Text className="text-muted">
                Modifique cuidadosamente
              </Form.Text>
              <Form.Label>Nombre</Form.Label>
              <Form.Control
                name="nombre"
                type="text"
                value={name}
                onChange={handleInput}
              />
              <Form.Label>Apellido Paterno</Form.Label>
              <Form.Control
                type="text"
                value={lastName}
                onChange={handleInput}
              />
              <Form.Label>Apellido Materno</Form.Label>
              <Form.Control
                type="text"
                value={secondLastName}
                onChange={handleInput}
              />
              <Form.Label>C.I</Form.Label>
              <Form.Control type="text" value={cii} onChange={handleInput} />
              <Form.Group
                // as={}
                controlId="formGridState"
              >
                <Form.Label>Genero</Form.Label>
                <Form.Control as="select" value={genero}>
                  {genero === "Female" ? (
                    <option>Femenino</option>
                  ) : (
                    <option>Masculino</option>
                  )}
                </Form.Control>
              </Form.Group>

              <Form.Label>Direccion</Form.Label>
              <Form.Control type="text" value={city} onChange={handleInput} />
            </Form.Group>
            <Button variant="primary" type="submit">
              Submit
            </Button>
          </Form>
        </Container>

        <Modal.Footer>
          <Button variant="secondary" onClick={handleClose}>
            Close
          </Button>
          <Button variant="primary" onClick={handleClose}>
            Save Changes
          </Button>
        </Modal.Footer>
      </Modal>
    </>
  );
}

export default ModalView;
