import React, { useState } from "react";
import { Table, Button, Modal, Form, Container } from "react-bootstrap";
import useInitialState from "../utils/hooks/useInitalState";
import userDelete from "../utils/hooks/delete";
const API = "http://localhost:3004/";

function TableApi() {
  const initialState = useInitialState(API);
  const eliminar = (id) => userDelete(id);

  return initialState === 0 ? (
    <h1>Loading....</h1>
  ) : (
    <Table striped bordered hover variant="dark">
      <thead>
        <tr>
          <th>#</th>
          <th>Nombre</th>
          <th>Apellido Paterno</th>
          <th>Apellido Materno</th>
          <th>C.I</th>
          <th>Genero</th>
          <th>Direccion</th>
          <th>Modificar</th>
          <th>Eliminar</th>
        </tr>
      </thead>
      <tbody>
        {initialState.usuarios.length > 0 &&
          initialState.usuarios.map((usuario, id) => (
            <React.Fragment key={id}>
              <tr>
                <th>{usuario.id_usuario}</th>
                <th>{usuario.nombre}</th>
                <th>{usuario.apellido_paterno}</th>
                <th>{usuario.apellido_materno}</th>
                <th>{usuario.ci}</th>
                <th>{usuario.genero}</th>
                <th>{usuario.direccion}</th>
                <th>
                  <Button
                    variant="success"
                    onClick={() => modificar(usuario.id_usuario)}
                  >
                    Modificar
                  </Button>
                </th>
                <th>
                  <Button
                    variant="danger"
                    onClick={() => eliminar(usuario.id_usuario)}
                  >
                    Eliminar
                  </Button>
                </th>
              </tr>
            </React.Fragment>
          ))}
      </tbody>
    </Table>
  );
}
function modificar(id_usuario) {
  const [show, setShow] = useState(false);

  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);

  const [form, setValues] = useState({
    nombre: "",
  });
  const handleInput = (event) => {
    setValues({
      ...form,
      [event.target.name]: event.target.value,
    });
  };
  console.log(form);
  const handleSubmit = (event) => {
    //matar el envio del formulario con prevent DEFAUTL
    console.log("enviado");
  };
  return (
    <>
      <Button variant="success" onClick={handleShow}>
        Modificar
      </Button>

      <Modal show={show} onHide={handleClose}>
        <Modal.Header closeButton>
          <Modal.Title>Modificar</Modal.Title>
        </Modal.Header>
        <Container fluid>
          <Form onSubmit={handleSubmit}>
            <Form.Group controlId="formBasicEmail">
              <Form.Text className="text-muted">
                Modifique cuidadosamente
              </Form.Text>
              <Form.Label>Nombre</Form.Label>
              <Form.Control name="nombre" type="text" onChange={handleInput} />
              <Form.Label>Apellido Paterno</Form.Label>
              <Form.Control type="text" onChange={handleInput} />
              <Form.Label>Apellido Materno</Form.Label>
              <Form.Control type="text" onChange={handleInput} />
              <Form.Label>C.I</Form.Label>
              <Form.Control type="text" onChange={handleInput} />
              {/* <Form.Group
                // as={}
                controlId="formGridState"
              >
                <Form.Label>Genero</Form.Label>
                <Form.Control as="select">
                  {genero === "Female" ? (
                    <option>Femenino</option>
                  ) : (
                    <option>Masculino</option>
                  )}
                </Form.Control>
              </Form.Group> */}

              <Form.Label>Direccion</Form.Label>
              <Form.Control type="text" onChange={handleInput} />
            </Form.Group>
            <Button variant="primary" type="submit">
              Submit
            </Button>
          </Form>
        </Container>

        <Modal.Footer>
          <Button variant="secondary" onClick={handleClose}>
            Close
          </Button>
          <Button variant="primary" onClick={handleClose}>
            Save Changes
          </Button>
        </Modal.Footer>
      </Modal>
    </>
  );
}

export default TableApi;
