import React, { useState } from "react";
import { Table, Button, Modal, Form } from "react-bootstrap";
import useInitialState from "../utils/hooks/useInitalState";
import userDelete from "../utils/hooks/delete";
// import ModalView from "../components/ModalView";
const API = "http://localhost:3004/";

function TableApi() {
  const initialState = useInitialState(API);
  const [stado, setStado] = useState();
  const [idUser, setIdUser] = useState();

  const eliminar = (id) => userDelete(id);
  function modificar(id, { valor }) {
    setStado(valor);
    setIdUser(id);

  }

  return initialState === 0 ? (
    <h1>Loading....</h1>
  ) : (
    <>
      <Table striped bordered hover variant="dark">
        <thead>
          <tr>
            <th>#</th>
            <th>Nombre</th>
            <th>Apellido Paterno</th>
            <th>Apellido Materno</th>
            <th>C.I</th>
            <th>Genero</th>
            <th>Direccion</th>
            <th>Modificar</th>
            <th>Eliminar</th>
          </tr>
        </thead>
        <tbody>
          {initialState.usuarios.length > 0 &&
            initialState.usuarios.map((usuario, id) => (
              <React.Fragment key={id}>
                <tr>
                  <th>{usuario.id_usuario}</th>
                  <th>{usuario.nombre}</th>
                  <th>{usuario.apellido_paterno}</th>
                  <th>{usuario.apellido_materno}</th>
                  <th>{usuario.ci}</th>
                  <th>{usuario.genero}</th>
                  <th>{usuario.direccion}</th>
                  <th>
                    <Button
                      variant="success"
                      onClick={() =>
                        modificar(usuario.id_usuario, { valor: true })
                      }
                    >
                      Modificar
                    </Button>
                  </th>
                  <th>
                    <Button
                      variant="danger"
                      onClick={() => eliminar(usuario.id_usuario)}
                    >
                      Eliminar
                    </Button>
                  </th>
                </tr>
              </React.Fragment>
            ))}
        </tbody>
        {stado === true ? (
          <Modal show={true}>
            <Modal.Header closeButton onClick={() => setStado(false)}>
              <Modal.Title>Modal heading</Modal.Title>
            </Modal.Header>
            <Modal.Body>
              Woohoo, you're reading this text in a modal!
            </Modal.Body>
            <Modal.Footer>
              <Button
                variant="secondary"
                onClick={() => {
                  setStado(false);
                }}
              >
                Close
              </Button>
              <Button variant="primary">Save Changes</Button>
            </Modal.Footer>
          </Modal>
        ) : (
          () => console.log("cerrado")
        )}
      </Table>
    </>
  );
}

export default TableApi;
